import React from 'react'
import ContentContainer from '../ContentContainer'

const imgList = [{ img_url: "../img/image.jpg" }]


const ImageContainer = (imgList) => {
    return (
        <ul className="img_list">
            {
                imgList.map((img, index) => {
                    return (
                        <li className="img_list_item" key={index}><img src={img.url} alt={img.name} /></li>
                    )
                })
            }
        </ul>

    )
}
const Content = () => {
    return (

        <div>content container</div>
    )
}

export default function BagDetails() {
    return (

        <><ContentContainer> <div className="container">
            <div className="row">
                <div className="col-md-2">
                    <ImageContainer imgList={imgList} />

                </div>
                <div className="col-md-6"><Content /></div>
            </div>
        </div>
        </ContentContainer></>
    )
}
