import React from 'react';

export default function Tags({ tags, onClick }) {
    const [category, setCategory] = React.useState()
    const click = (bag_type) => {
        setCategory(bag_type)
        onClick(category)
    }
    return (
        <div className="tag_btn_group">
            {
                tags.map((tag, index) => {
                    return (
                        <div key={index}>
                            <button className="tag_btn" onClick={() => olick(tag.bag_type)}>{tag.label}</button>
                        </div>
                    )
                })
            }
        </div>
    )
}
