import Navbar from '../Navbar';
import { connect } from 'react-redux'
import Tags from '../Tags';
import ContentContainer from '../ContentContainer';
import Card from './Card';
import BagsTypeSelect from "../common/Select"
//import { useDispatch, useSelector } from 'react-redux';
import { getProducts } from '../../actions/products';
import PropTypes from 'prop-types';

const bagTypes = [{
    label: "All", link: "", bag_type: "",

}, {
    label: "Saddle Bags", link: "", bag_type: "sling_bag"
},
{
    label: "Tote Bags", link: "", bag_type: "tote_bag"
},
{
    label: "Bucket Bags", link: "", bag_type: "sling_bag"
},
{
    label: "Messenger Bags", link: "", bag_type: "sling_bag"
}]

