// { title: "item1", content: "Timberland Women's RFID Leather Crossbody Bag Wallet Purse", image: require("../img/image.jpg"), sub_images: [{}], stars: 3, review_count: 100, original_price: 200000, offer_price: 567890, shipping_info: "free shippigsdfsdfvsdvfs hjsfjshd dsfhshfhsd", size: "10 X 15 Inch", keypoints: ["dsfsdfs", "sdfsdds", "dfsgfg", "guyiuyiu"] },
// { title: "item1", content: "Timberland Women's RFID Leather Crossbody Bag Wallet Purse", image: require("../img/image.jpg"), sub_images: [{}], stars: 3, review_count: 100, original_price: 20, offer_price: 0, shipping_info: "free shippigsdfsdfvsdvfs hjsfjshd dsfhshfhsd", product_size: "10 X 15 Inch", keypoints: ["dsfsdfs", "sdfsdds", "dfsgfg", "guyiuyiu"] },
// { title: "item1", content: "Timberland Women's RFID Leather Crossbody Bag Wallet Purse", image: require("../img/image.jpg"), sub_images: [{}], stars: 3, review_count: 100, original_price: 20, offer_price: 0, shipping_info: "free shippigsdfsdfvsdvfs hjsfjshd dsfhshfhsd", size: "10 X 15 Inch", keypoints: ["dsfsdfs", "sdfsdds", "dfsgfg", "guyiuyiu"] },
// { title: "item1", content: "Timberland Women's RFID Leather Crossbody Bag Wallet Purse", image: require("../img/image.jpg"), sub_images: [{}], stars: 3, review_count: 100, original_price: 20, offer_price: 0, shipping_info: "free shippigsdfsdfvsdvfs hjsfjshd dsfhshfhsd", size: "10 X 15 Inch", keypoints: ["dsfsdfs", "sdfsdds", "dfsgfg", "guyiuyiu"] }]
// const list = [{
//     product_content: "xvxcvsdfdsfsd dsfjsidf jfsjfs dsfsdjfsdj",
//     product_image: "https://s3.ap-southeast-1.amazonaws.com/agni.images/image.jpg",
//     product_keypoints: ['skdfksdfklsd', 'fsfsdfsdfds'],
//     product_name: "hdfhdfhd",
//     product_offer_price: 234598,
//     product_original_price: 2344,
//     product_review_count: "234",
//     product_size: "14*25",
//     product_stars: 5
// }]

function Bags({ getProducts, productsList }) {
    const [displaybagslist, setDisplayBagsList] = React.useState()
    const [displayalllist, setDisplayAllList] = React.useState(false)
    console.log('displaybagslist', displaybagslist)
    console.log('productsList', productsList)
    const handleClick = (selectedBagType) => {
        setDisplayAllList(true)
        let listOfBags = productsList;
        if (selectedBagType == "") {
            console.log(selectedBagType)
            setDisplayBagsList(productsList)
        } else {
            const filteredBags = listOfBags.filter((item, index) => {
                return item.product_type == selectedBagType
            })
            setDisplayBagsList(filteredBags)
        }
    }


    React.useEffect(() => {
        getProducts()
        // handleClick()

    }, [getProducts])

    return (<><Navbar />
        <ContentContainer>
            <div className='container'>
                <div className='row d-none d-sm-block'>
                    <div className='col'>
                        <Tags tags={bagTypes} onClick={handleClick} />
                    </div>
                </div>
                <div className='row d-md-none'>
                    <BagsTypeSelect dropdownListItems={bagTypes} />
                </div>
                <div className='row' style={{ marginTop: '20px' }}>
                    {!displayalllist ? <Card list={productsList} /> : <Card list={displaybagslist} />}
                </div>
            </div>
        </ContentContainer></>);
}

Bags.propTypes = {
    getProducts: PropTypes.func.isRequired,
    productsList: PropTypes.array
};

const mapStateToProps = state => {
    return {
        productsList: state.products.products
    }
}

export default connect(mapStateToProps, { getProducts })(Bags);
