import React from 'react'
import Rating from './Rating'


export default function Card({ list }) {
    return (
        <>
            {list.map((item, index) => {
                return (
                    <div className='col-md-4' key={index}>
                        <div className="card" >
                            <img src={item.image} className="card-img-top" alt={item.title} height="250px" />
                            <div className="card-body">
                                <h5 className="card-title">{item.title}</h5>
                                <div className='row justify-content-start'>
                                    <p className='col-md-6 item_info'>
                                        <span style={{ display: 'flex', justifyContent: 'space-between' }}><Rating stars={item.stars} /> {item.review_count ? item.review_count : "0"}</span>
                                    </p>
                                </div>
                                <p className='col-md-12 p-0 m-0 item_price'>
                                    {`HKD ${item.original_price}`}
                                </p>
                                <p className="card-text pt-0">{item.content}</p>
                                <p className="card-text pt-0">
                                    Free Shipping {`${item.shipping_info}`}</p>
                            </div>
                        </div>
                    </div>
                )
            })}

        </>
    )
}
