import React from 'react'
import ContentContainer from '../ContentContainer'
import { getProductById } from '../../actions/products'
import { connect } from 'react-redux';
import PropTypes from 'prop-types'

const ImageContainer = ({ imgList }) => {
    const [selectedImage, setSelectedImage] = React.useState()
    const handleClick = (image) => {
        console.log('image :>> ', image);
        setSelectedImage(image)
    }
    //console.log('imgList :>> ', imgList);
    React.useEffect(() => {
        imgList && imgList.forEach((img, index) => {
            if (index === 0) {
                setSelectedImage(img)
            }
        })
    }, [imgList]);
    return (
        <>
            <ul className="img_list p-0 m-0">
                {
                    imgList ? imgList.map((img, index) => {
                        return (

                            <li key={index}><img src={img} alt={img} height="60px" weight="60px" className="img_list_item" onClick={() => handleClick(img)} /></li>
                        )
                    }) : <p>image not available</p>
                }
            </ul>
            <img src={selectedImage} alt="product_img" height="60px" weight="60px" className="img_list_item" />
        </>


    )
}
// const Content = () => {
//     return (
//         <div></div>
//     )
// }

function BagDetails({ getProductById, productInfo: { product_name, product_description, product_sub_images } }) {
    //console.log('product_sub_images :>> ', product_sub_images);
    return (
        <>
            <ContentContainer>
                <div className="container p-0 m-0">
                    <div className="row justify-content-start p-0 m-0">
                        <div className="col-md-6 p-0 m-0">
                            {product_sub_images && product_sub_images.length > 0 && <ImageContainer imgList={product_sub_images} />}
                        </div>
                        {/* <div className="col-md-6"><Content /></div> */}
                    </div>
                </div>
            </ContentContainer>
        </>
    )

}
BagDetails.propTypes = {
    getProductbyID: PropTypes.func
}

const mapStateToProps = state => {
    // console.log('state :>> ', state);
    return {
        productInfo: state.products.product
    }
}

export default connect(mapStateToProps, { getProductById })(BagDetails)
