import api from '../utils/api';
import { setAlert } from './alert';
import {
  GET_PRODUCTS,
  GET_PRODUCT_BY_ID,
} from './types';

export const getProducts = () => async (dispatch) => {
  try {
    const res = await api.get('/bags');
    console.log(res)
    dispatch({
      type: GET_PRODUCTS,
      payload: res.data
    });
  } catch (err) {
    // console.log(err)
    const errors = err.response.data.errors;
    if (errors) {
      errors.forEach((error) => dispatch(setAlert(error.msg, 'danger')));
    }
    //   dispatch({
    //     type: REGISTER_FAIL
    //   });
  }
};
export const getProductById = (body) => async (dispatch) => {

  const body = { id }
  console.log(body)
  try {
    const res = await api.post('/bags', body)
    console.log(res)
  } catch (error) {
    console.log('error', error.message)
    console.log("server error!!")
  }
}
