import React from 'react'
import { NavLink } from 'react-router-dom'

export default function Dropdown({ dropdownListItems, onChange }) {
    return (
        <select className="form-select" aria-label="Default select example" onChange={(e) => onChange(e.target.value)}>
            <option value="">Types</option>
            {
                dropdownListItems.map((listItem, index) => {
                    return (
                        <option key={index} value={listItem.type} className="form-select-option" >{listItem.label} </option>
                    )
                })
            }
        </select>
    )
}
