import React from 'react'
import { NavLink } from 'react-router-dom'

export default function Dropdown({ dropdownListItems,onClick }) {
    const [selected, setSelected] = React.useState()
    return (
        <select className="form-select" aria-label="Default select example">
            <option value="">Types</option>
            {
                dropdownListItems.map((listItem, index) => {
                    return (
                        <option key={index} value={listItem.type} className="form-select-option" onClick={() => onClick(listItem.type)}>{listItem.label} </option>
                    )
                })
            }
        </select>
    )
}
