import { GET_PRODUCTS } from "../actions/types";

const products = []

function productsReducer(state = initialState, action) {
    const { type, payload } = action
    switch (type) {
        case GET_PRODUCTS:
            return {
                ...state,
                products: payload
            }
        default:
            return {
                ...state
            }

    }
}
export default productsReducer
