import React from 'react';
import Image1 from '../img/image.jpg';
import Navbar from './Navbar';

const Register = () => {
    const [user, setUser] = React.useState({
        user_name: '',
        user_email: "",
        user_password: ""
    })
    console.log(user)
    const handleChange = (e) => {
        setUser(prevState => ({ prevState, user_name: e.target.value }))
    }
    const handleEmailChange = (e) => {
        setUser(prevState => ({ prevState, user_email: e.target.value }))
    }
    const handlePasswordChange = (e) => {
        setUser(prevState => ({ prevState, user_password: e.target.value }))
    }
    const handleReset = () => {
        setUser({ user_name: "", user_email: "", user_password: "" })
    }

    const handleSubmit = (e) => {
        e.preventDefault()
        console.log(user)
        handleReset()
    }
    return (<>
        <Navbar />
        <div className='container register_container'>
            <div className="row justify-content-between align-items-center">
                <div className="col-md-6">
                    <img src={Image1} className="d-none d-md-block w-100 register_img" alt="Register_img" style={{ height: '400px' }} />
                </div>
                <div className="col-md-5">
                    <h1 className="register_title">Register</h1>
                    <form onSubmit={handleSubmit}>
                        <div className="mb-3">
                            <label htmlFor="user_name" className="form-label">Name</label>
                            <input type="text" className="form-control" id="user_name" aria-describedby="username" onChange={handleChange} />
                        </div>
                        <div className="mb-3">
                            <label htmlFor="user_email" className="form-label">Email</label>
                            <input type="email" className="form-control" id="user_email" aria-describedby="emailHelp" onChange={handleEmailChange} />
                        </div>
                        <div className="mb-3">
                            <label htmlFor="user_password" className="form-label">Password</label>
                            <input type="password" className="form-control" id="user_password" onChange={handlePasswordChange} />
                        </div>
                        <button type="submit" className="btn btn-primary btn-block btn-lg auth_btn mt-5 mt-md-5  d-grid">Submit</button>
                    </form>
                </div>
            </div>
        </div>
    </>);
}

export default Register;
