import React from 'react'
import ContentContainer from '../ContentContainer'
import { getProductById } from '../../actions/products'
import { connect } from 'react-redux';
import PropTypes from 'prop-types'




const ImageContainer = ({ imgList }) => {
    return (
        <ul className="img_list">
            {
                imgList.map((img, index) => {
                    return (
                        <li className="img_list_item" key={index}><img src={`require(${img})`} alt={img} /></li>
                    )
                })
            }
        </ul>

    )
}
const Content = () => {
    return (

        <div>content container</div>
    )
}

function BagDetails({ getProductById, productInfo: { product_name, product_description, product_sub_images

} }) {
    console.log('productInfo :>> ', product_name);
    return (

        <><ContentContainer>

            <div className="container">
                <div className="row">
                    <div className="col-md-2">
                        {product_sub_images && product_sub_images.length > 0 && <ImageContainer imagList={product_sub_images} />}
                    </div>
                    <div className="col-md-6"><Content /></div>
                </div>
            </div>

        </ContentContainer></>
    )

}
BagDetails.propTypes = {
    getProductbyID: PropTypes.func
}

const mapStateToProps = state => {
    console.log('state :>> ', state);
    return {
        productInfo: state.products.product
    }
}

export default connect(mapStateToProps, { getProductById })(BagDetails)
